import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaBars } from "react-icons/fa";
import "../styles.css";
import logo from "../assets/logo.png";
import Alerts from "../components/Alerts";
import Dropdown from "../components/Dropdown";

function Dashboard({ onLogout }) {
  const navigate = useNavigate();

  const [floor, setFloor] = useState("");
  const [department, setDepartment] = useState("");
  const [alerts, setAlerts] = useState([
    {
      id: 1,
      name: "John Doe",
      vital: "High BP",
      floor: "1st Floor",
      department: "Cardiology",
      acknowledgedBy: null,
      acknowledgedAt: null,
    },
    {
      id: 2,
      name: "Jane Smith",
      vital: "Low Oxygen",
      floor: "2nd Floor",
      department: "Neurology",
      acknowledgedBy: null,
      acknowledgedAt: null,
    },
  ]);

  const [showFloorDropdown, setShowFloorDropdown] = useState(false);
  const [showDepartmentDropdown, setShowDepartmentDropdown] = useState(false);
  const [showAbout, setShowAbout] = useState(false);

  const acknowledgeAlert = (id, doctor) => {
    setAlerts(
      alerts.map((alert) => {
        if (alert.id === id) {
          return {
            ...alert,
            acknowledgedBy: doctor,
            acknowledgedAt: new Date().toLocaleString(),
          };
        }
        return alert;
      })
    );
  };

  const handleLogout = () => {
    onLogout();
    navigate("/");
  };

  const viewPatients = () => {
    if (floor && department) {
      navigate("/patients", { state: { floor, department } });
    } else {
      alert("Please select both floor and department.");
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      const aboutSection = document.getElementById("about-us");
      if (aboutSection) {
        const aboutTop = aboutSection.getBoundingClientRect().top;
        if (aboutTop < window.innerHeight - 100) {
          setShowAbout(true);
        } else {
          setShowAbout(false);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="dashboard-container">
      <header>
        <img src={logo} alt="WELLSENSE Logo" className="dashboard-logo" />
        <h2>WELLSENSE</h2>
        <div className="top-right">
          {/* Floor Dropdown */}
          <div className="dropdown-container">
            <button
              onClick={() => setShowFloorDropdown(!showFloorDropdown)}
              className="hamburger-button"
            >
              <FaBars size={20} />
            </button>
            <span className="dropdown-label-box">Floor</span>
            {showFloorDropdown && (
              <Dropdown
                options={["1st Floor", "2nd Floor"]}
                onChange={(value) => {
                  setFloor(value);
                  setShowFloorDropdown(false);
                }}
              />
            )}
          </div>
          {/* Department Dropdown */}
          <div className="dropdown-container">
            <button
              onClick={() => setShowDepartmentDropdown(!showDepartmentDropdown)}
              className="hamburger-button"
            >
              <FaBars size={20} />
            </button>
            <span className="dropdown-label-box">Department</span>
            {showDepartmentDropdown && (
              <Dropdown
                options={["Cardiology", "Nephrology", "Neurology"]}
                onChange={(value) => {
                  setDepartment(value);
                  setShowDepartmentDropdown(false);
                }}
              />
            )}
          </div>
          {/* Alerts */}
          <div onClick={() => {}} style={{ cursor: "pointer", marginRight: "10px" }}>
            <Alerts alerts={alerts} onAcknowledge={acknowledgeAlert} />
          </div>
          {/* Logout Button */}
          <div className="logout-container">
            <button onClick={handleLogout} className="logout-button">
              Logout
            </button>
          </div>
        </div>
      </header>

      <div className="content-container">
        {floor && department && (
          <button onClick={viewPatients} className="view-patients-button">
            View Patients
          </button>
        )}
      </div>

      {/* About Us Section (Added) */}
      <div id="about-us" className={`about-section ${showAbout ? "visible" : ""}`}>
        <h2>About Us</h2>
        <p>
          <strong>WELLSENSE</strong> is an innovative IoT-based remote health monitoring system
          designed to track real-time patient vitals. Our mission is to enhance
          healthcare efficiency and improve patient outcomes using smart
          technology.
        </p>
        <h3>Our Mission</h3>
        <p>
          At WELLSENSE, our mission is to provide healthcare professionals with
          real-time access to critical patient data. Our system ensures that
          medical teams can react swiftly to patient needs, improving the quality
          of care and reducing response times.
        </p>
        <h3>Contact Us</h3>
        <p>Email: <a href="mailto:info@wellsense.com">info@wellsense.com</a></p>
      </div>

      {/* Background Slideshow */}
      <div className="background-slideshow">
        <div className="slide" style={{ backgroundImage: "url('/bg1.jpg')" }}></div>
        <div className="slide" style={{ backgroundImage: "url('/bg2.jpg')" }}></div>
        <div className="slide" style={{ backgroundImage: "url('/bg3.jpg')" }}></div>
      </div>
    </div>
  );
}

export default Dashboard;
