import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles.css";
import logo from "../assets/logo.png"; // Moved to assets folder
import Alerts from "../components/Alerts";
import Dropdown from "../components/Dropdown";
import PatientList from "./PatientList";

function Dashboard({ onLogout }) {
  const navigate = useNavigate();
  const [floor, setFloor] = useState("1st Floor");
  const [department, setDepartment] = useState("Nephrology");
  const [alerts, setAlerts] = useState([
    { id: 1, name: "John Doe", vital: "High BP", doctor: "Dr. Monica" },
    { id: 2, name: "Jane Smith", vital: "Low Oxygen", doctor: "Dr. Rachel" },
  ]);

  const acknowledgeAlert = (id) => {
    setAlerts(alerts.filter((alert) => alert.id !== id));
  };

  const handleLogout = () => {
    onLogout(); // Calls logout function from App.js
    navigate("/"); // Redirects to login page
  };

  return (
    <div className="dashboard-container">
      <header>
        <img src={logo} alt="WELLSENSE Logo" className="dashboard-logo" />
        <h2>WELLSENSE</h2>
        <div className="top-right">
          <Dropdown
            label="Floor"
            options={["1st Floor", "2nd Floor"]}
            selected={floor}
            onChange={setFloor}
          />
          <Dropdown
            label="Department"
            options={["Nephrology", "Cardiology", "Neurology"]}
            selected={department}
            onChange={setDepartment}
          />
          <Alerts alerts={alerts} onAcknowledge={acknowledgeAlert} />

          {/* Logout Section */}
          <div className="logout-container">
            <button onClick={handleLogout} className="logout-button">
              Logout
            </button>
          </div>
        </div>
      </header>
      <PatientList floor={floor} department={department} />
      <div className="background-image"></div>
    </div>
  );
}

export default Dashboard;
