import React, { useState } from "react";
import { FaBell } from "react-icons/fa";

function Alerts({ alerts, onAcknowledge }) {
  const [modalOpen, setModalOpen] = useState(false);

  const toggleModal = () => {
    setModalOpen(!modalOpen);
  };

  return (
    <div className="alerts">
      {/* Bell icon with badge */}
      <div
        className="bell-icon"
        onClick={toggleModal}
        style={{ cursor: "pointer", position: "relative" }}
      >
        <FaBell size={24} />
        {alerts.length > 0 && (
          <span
            className="badge"
            style={{
              position: "absolute",
              top: -5,
              right: -5,
              backgroundColor: "red",
              color: "white",
              borderRadius: "50%",
              padding: "2px 5px",
              fontSize: "12px",
            }}
          >
            {alerts.length}
          </span>
        )}
      </div>

      {/* Modal for abnormal alerts */}
      {modalOpen && (
        <div
          className="modal-overlay"
          onClick={toggleModal}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: "rgba(0, 0, 0, 0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
        >
          <div
            className="modal-content"
            onClick={(e) => e.stopPropagation()}
            style={{
              backgroundColor: "white",
              padding: "20px",
              borderRadius: "8px",
              maxWidth: "400px",
              width: "90%",
              color: "black", // Set text color to black for all content
            }}
          >
            <h2 style={{ color: "black" }}>Abnormal Alerts</h2>
            {alerts.length > 0 ? (
              alerts.map((alert) => (
                <div
                  key={alert.id}
                  className="alert-item"
                  style={{
                    marginBottom: "15px",
                    borderBottom: "1px solid #ccc",
                    paddingBottom: "10px",
                  }}
                >
                  <p style={{ color: "black" }}>
                    <strong>{alert.name}</strong> â€“ {alert.vital}
                    <br />
                    <em>Acknowledged by: {alert.doctor}</em>
                  </p>
                  <button
                    onClick={() => onAcknowledge(alert.id)}
                    style={{
                      padding: "5px 10px",
                      backgroundColor: "#007bff",
                      color: "white",
                      border: "none",
                      borderRadius: "4px",
                      cursor: "pointer",
                    }}
                  >
                    Acknowledge
                  </button>
                </div>
              ))
            ) : (
              <p style={{ color: "black" }}>No alerts</p>
            )}
            <button
              onClick={toggleModal}
              className="back-button"
              style={{
                marginTop: "10px",
                padding: "5px 10px",
                backgroundColor: "red",
                color: "white",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer",
              }}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Alerts;
