import React, { useState } from "react";
import { FaBell } from "react-icons/fa";

function Alerts({ alerts, onAcknowledge }) {
  const [modalOpen, setModalOpen] = useState(false);

  const toggleModal = () => {
    setModalOpen(!modalOpen);
  };

  return (
    <div className="alerts">
      {/* Bell icon triggers the modal */}
      <div className="bell-icon" onClick={toggleModal} style={{ cursor: "pointer" }}>
        <FaBell size={24} />
        {alerts.length > 0 && <span className="badge">{alerts.length}</span>}
      </div>

      {/* Modal for abnormal alerts */}
      {modalOpen && (
        <div className="modal-overlay" onClick={toggleModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2>Abnormal Alerts</h2>
            {alerts.length > 0 ? (
              alerts.map((alert) => (
                <div key={alert.id} className="alert-item">
                  <p>
                    <strong>{alert.name}</strong> - {alert.vital}
                    <br />
                    <em>Acknowledged by: {alert.doctor}</em>
                  </p>
                  <button onClick={() => onAcknowledge(alert.id)}>Acknowledge</button>
                </div>
              ))
            ) : (
              <p>No alerts</p>
            )}
            <button onClick={toggleModal} className="back-button">
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Alerts;
