import React, { useState } from "react";
import { FaBell } from "react-icons/fa";

function Alerts({ alerts, onAcknowledge }) {
  const [modalOpen, setModalOpen] = useState(false);

  const toggleModal = () => {
    setModalOpen(prev => !prev);
  };

  return (
    <div className="alerts">
      {/* Bell icon triggers the modal */}
      <div className="bell-icon" onClick={toggleModal} style={{ cursor: "pointer", position: "relative" }}>
        <FaBell size={24} />
        {alerts.length > 0 && <span className="badge">{alerts.length}</span>}
      </div>

      {/* Modal for abnormal alerts */}
      {modalOpen && (
        <div className="modal-overlay" onClick={toggleModal}>
          <div 
            className="modal-content" 
            onClick={(e) => e.stopPropagation()} 
            style={{
              background: "white",
              padding: "20px",
              borderRadius: "8px",
              maxWidth: "400px",
              margin: "50px auto"
            }}
          >
            <h2>Abnormal Alerts</h2>
            {alerts.length > 0 ? (
              alerts.map((alert) => (
                <div 
                  key={alert.id} 
                  className="alert-item" 
                  style={{
                    marginBottom: "15px",
                    borderBottom: "1px solid #ccc",
                    paddingBottom: "10px"
                  }}
                >
                  <p>
                    <strong>{alert.name || "Patient Name"}</strong> â€“ {alert.vital || "Vital information not available"}
                    <br />
                    <em>Acknowledged by: {alert.doctor || "N/A"}</em>
                  </p>
                  <button 
                    onClick={() => onAcknowledge(alert.id)}
                    style={{
                      marginTop: "5px",
                      padding: "5px 10px",
                      backgroundColor: "#007bff",
                      color: "white",
                      border: "none",
                      borderRadius: "4px",
                      cursor: "pointer"
                    }}
                  >
                    Acknowledge
                  </button>
                </div>
              ))
            ) : (
              <p>No alerts</p>
            )}
            <button 
              onClick={toggleModal} 
              className="back-button"
              style={{
                marginTop: "10px",
                padding: "5px 10px",
                backgroundColor: "red",
                color: "white",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer"
              }}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Alerts;
