import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles.css";
import logo from "../assets/logo.png"; // Your logo in assets folder
import Alerts from "../components/Alerts";
import Dropdown from "../components/Dropdown"; // Hamburger version of Dropdown

function Dashboard({ onLogout }) {
  const navigate = useNavigate();
  const [floor, setFloor] = useState("1st Floor");
  const [department, setDepartment] = useState("Nephrology");
  const [alerts, setAlerts] = useState([
    { id: 1, name: "John Doe", vital: "High BP", doctor: "Dr. Monica" },
    { id: 2, name: "Jane Smith", vital: "Low Oxygen", doctor: "Dr. Rachel" },
  ]);

  const acknowledgeAlert = (id) => {
    setAlerts(alerts.filter((alert) => alert.id !== id));
  };

  const handleLogout = () => {
    onLogout();
    navigate("/");
  };

  // Navigate to PatientPage with the selected floor and department
  const viewPatients = () => {
    // Ensure both selections are made if necessary
    navigate("/patients", { state: { floor, department } });
  };

  return (
    <div className="dashboard-container">
      <header>
        <img src={logo} alt="WELLSENSE Logo" className="dashboard-logo" />
        <h2>WELLSENSE</h2>
        <div className="top-right">
          <Dropdown
            label="Floor"
            options={["1st Floor", "2nd Floor"]}
            selected={floor}
            onChange={setFloor}
          />
          <Dropdown
            label="Department"
            options={["Nephrology", "Cardiology", "Neurology"]}
            selected={department}
            onChange={setDepartment}
          />
          <div onClick={() => {}} style={{ cursor: "pointer" }}>
            {/* Clicking the Alerts component should open a dialog/modal in your implementation */}
            <Alerts alerts={alerts} onAcknowledge={acknowledgeAlert} />
          </div>
          <div className="logout-container">
            <button onClick={handleLogout} className="logout-button">
              Logout
            </button>
          </div>
        </div>
      </header>
      <div className="content-container">
        <button onClick={viewPatients} className="view-patients-button">
          View Patients
        </button>
      </div>
      <div className="background-image"></div>
    </div>
  );
}

export default Dashboard;
