function Dashboard({ onLogout }) {
  const navigate = useNavigate();
  
  // Initialize selections as empty so user must select explicitly
  const [floor, setFloor] = useState("");
  const [department, setDepartment] = useState("");
  const [alerts, setAlerts] = useState([
    { id: 1, name: "John Doe", vital: "High BP", doctor: "Dr. Monica" },
    { id: 2, name: "Jane Smith", vital: "Low Oxygen", doctor: "Dr. Rachel" },
  ]);
  
  // State to control dropdown visibility
  const [showFloorDropdown, setShowFloorDropdown] = useState(false);
  const [showDepartmentDropdown, setShowDepartmentDropdown] = useState(false);

  const acknowledgeAlert = (id) => {
    setAlerts(alerts.filter((alert) => alert.id !== id));
  };

  const handleLogout = () => {
    onLogout(); // Calls logout function from App.js
    navigate("/"); // Redirects to login page
  };

  // Navigate to PatientPage with the selected floor and department
  const viewPatients = () => {
    if (floor && department) {
      navigate("/patients", { state: { floor, department } });
    } else {
      alert("Please select both floor and department.");
    }
  };

  return (
    <div className="dashboard-container">
      <header>
        <img src={logo} alt="WELLSENSE Logo" className="dashboard-logo" />
        <h2>WELLSENSE</h2>
        <div className="top-right">
          {/* Floor Dropdown with Hamburger Icon and Label */}
          <div className="dropdown-container" style={{ position: "relative", marginRight: "10px" }}>
            <button onClick={() => setShowFloorDropdown(!showFloorDropdown)} className="hamburger-button" style={{ background: "none", border: "none", cursor: "pointer" }}>
              <FaBars size={20} />
            </button>
            <span style={{ marginLeft: "5px" }}>Floor</span>
            {showFloorDropdown && (
              <Dropdown
                label=""
                options={["1st Floor", "2nd Floor"]}
                selected={floor}
                onChange={(value) => { setFloor(value); setShowFloorDropdown(false); }}
              />
            )}
          </div>
          {/* Department Dropdown with Hamburger Icon and Label */}
          <div className="dropdown-container" style={{ position: "relative", marginRight: "10px" }}>
            <button onClick={() => setShowDepartmentDropdown(!showDepartmentDropdown)} className="hamburger-button" style={{ background: "none", border: "none", cursor: "pointer" }}>
              <FaBars size={20} />
            </button>
            <span style={{ marginLeft: "5px" }}>Department</span>
            {showDepartmentDropdown && (
              <Dropdown
                label=""
                options={["Cardiology", "Nephrology", "Neurology"]}
                selected={department}
                onChange={(value) => { setDepartment(value); setShowDepartmentDropdown(false); }}
              />
            )}
          </div>
          {/* Alerts (clicking bell icon can open a modal in your implementation) */}
          <div onClick={() => {}} style={{ cursor: "pointer", marginRight: "10px" }}>
            <Alerts alerts={alerts} onAcknowledge={acknowledgeAlert} />
          </div>
          {/* Logout Button */}
          <div className="logout-container">
            <button onClick={handleLogout} className="logout-button">
              Logout
            </button>
          </div>
        </div>
      </header>
      <div className="content-container">
        {/* Show View Patients button only if both selections are made */}
        {floor && department && (
          <button onClick={viewPatients} className="view-patients-button">
            View Patients
          </button>
        )}
      </div>
      <div className="background-image"></div>
    </div>
  );
}

export default Dashboard;
